@tailwind base;
@tailwind components;
@tailwind utilities;

/* Hide scrollbar but keep scrolling functionality */
.scrollbar-hide {
  -ms-overflow-style: none;
  scrollbar-width: none;
}
.scrollbar-hide::-webkit-scrollbar {
  display: none;
}

/* Auto-scrolling animation */
@keyframes scroll-left {
  0% {
    transform: translateX(0);
  }
  100% {
    transform: translateX(-50%);
  }
}

.auto-scroll-wrapper {
  overflow: hidden;
  position: relative;
}

.auto-scroll-wrapper::before,
.auto-scroll-wrapper::after {
  content: '';
  position: absolute;
  top: 0;
  bottom: 0;
  width: 60px;
  z-index: 10;
  pointer-events: none;
}

.auto-scroll-wrapper::before {
  left: 0;
  background: linear-gradient(to right, hsl(174 60% 90%), transparent);
}

.auto-scroll-wrapper::after {
  right: 0;
  background: linear-gradient(to left, hsl(174 60% 90%), transparent);
}

.auto-scroll {
  display: flex;
  width: max-content;
  animation: scroll-left 25s linear infinite;
}

.auto-scroll:hover {
  animation-play-state: paused;
}

/* Entrance Animations */
@keyframes fade-in-up {
  0% {
    opacity: 0;
    transform: translateY(30px);
  }
  100% {
    opacity: 1;
    transform: translateY(0);
  }
}

@keyframes fade-in-scale {
  0% {
    opacity: 0;
    transform: scale(0.95);
  }
  100% {
    opacity: 1;
    transform: scale(1);
  }
}

@keyframes slide-in-left {
  0% {
    opacity: 0;
    transform: translateX(-30px);
  }
  100% {
    opacity: 1;
    transform: translateX(0);
  }
}

@keyframes slide-in-right {
  0% {
    opacity: 0;
    transform: translateX(30px);
  }
  100% {
    opacity: 1;
    transform: translateX(0);
  }
}

@keyframes icon-bounce {
  0%, 100% {
    transform: scale(1);
  }
  50% {
    transform: scale(1.1);
  }
}

.animate-fade-in-up {
  animation: fade-in-up 0.8s ease-out forwards;
}

.animate-fade-in-scale {
  animation: fade-in-scale 0.6s ease-out forwards;
}

.animate-slide-in-left {
  animation: slide-in-left 0.6s ease-out forwards;
}

/* Card Stagger Animations */
.card-stagger-1 {
  animation: fade-in-up 0.6s ease-out 0.1s forwards;
  opacity: 0;
}

.card-stagger-2 {
  animation: fade-in-up 0.6s ease-out 0.2s forwards;
  opacity: 0;
}

.card-stagger-3 {
  animation: fade-in-up 0.6s ease-out 0.3s forwards;
  opacity: 0;
}

.card-stagger-4 {
  animation: fade-in-up 0.6s ease-out 0.4s forwards;
  opacity: 0;
}

.card-stagger-5 {
  animation: fade-in-up 0.6s ease-out 0.5s forwards;
  opacity: 0;
}

.animate-slide-in-right {
  animation: slide-in-right 0.6s ease-out forwards;
}

.animate-delay-100 {
  animation-delay: 100ms;
}

.animate-delay-200 {
  animation-delay: 200ms;
}

.animate-delay-300 {
  animation-delay: 300ms;
}

.animate-delay-400 {
  animation-delay: 400ms;
}

.animate-delay-500 {
  animation-delay: 500ms;
}

/* Icon Glow Animation */
@keyframes icon-glow {
  0%, 100% {
    box-shadow: 0 0 20px rgba(255, 255, 255, 0.3), 0 0 40px rgba(255, 255, 255, 0.1);
  }
  50% {
    box-shadow: 0 0 30px rgba(255, 255, 255, 0.5), 0 0 60px rgba(255, 255, 255, 0.2);
  }
}

.icon-glow {
  animation: icon-glow 3s ease-in-out infinite;
}

/* Fade down animation */
@keyframes fade-down {
  0% {
    opacity: 0;
    transform: translateY(-20px);
  }
  100% {
    opacity: 1;
    transform: translateY(0);
  }
}

.animate-fade-down {
  animation: fade-down 0.8s ease-out forwards;
}

.icon-hover:hover {
  animation: icon-bounce 0.4s ease-in-out;
}

/* Wave divider */
.wave-divider {
  position: absolute;
  bottom: 0;
  left: 0;
  width: 100%;
  overflow: hidden;
  line-height: 0;
}

.wave-divider svg {
  position: relative;
  display: block;
  width: calc(100% + 1.3px);
  height: 60px;
}

/* LIGHT MODE */
:root {
  --button-outline: rgba(0,0,0, .10);
