                                      <SelectItem value="9">Grade 9</SelectItem>
                                      <SelectItem value="10">Grade 10</SelectItem>
                                      <SelectItem value="11">Grade 11</SelectItem>
                                      <SelectItem value="12">Grade 12</SelectItem>
                                    </SelectContent>
                                  </Select>
                                  <FormMessage />
                                </FormItem>
                              )}
                            />
                          </motion.div>
                        )}
                      </AnimatePresence>

                      <AnimatePresence>
                        {participantType === "college-student" && (
                          <motion.div 
                            className="grid grid-cols-1 sm:grid-cols-2 gap-6"
                            initial={{ opacity: 0, height: 0 }}
                            animate={{ opacity: 1, height: "auto" }}
                            exit={{ opacity: 0, height: 0 }}
                            transition={{ duration: 0.3 }}
                          >
                            <FormField
                              control={form.control}
                              name="collegeCourse"
                              render={({ field }) => (
                                <FormItem>
                                  <FormLabel>Course</FormLabel>
                                  <FormControl>
                                    <Input 
                                      placeholder="e.g., B.Tech CSE, BCA, B.Sc" 
                                      {...field} 
                                      data-testid="input-college-course"
                                    />
                                  </FormControl>
                                  <FormMessage />
                                </FormItem>
                              )}
                            />
                            <FormField
                              control={form.control}
                              name="collegeYear"
                              render={({ field }) => (
                                <FormItem>
                                  <FormLabel>Year</FormLabel>
                                  <Select onValueChange={field.onChange} defaultValue={field.value}>
                                    <FormControl>
                                      <SelectTrigger data-testid="select-college-year">
                                        <SelectValue placeholder="Select year" />
                                      </SelectTrigger>
                                    </FormControl>
                                    <SelectContent>
                                      <SelectItem value="1">1st Year</SelectItem>
                                      <SelectItem value="2">2nd Year</SelectItem>
                                      <SelectItem value="3">3rd Year</SelectItem>
                                      <SelectItem value="4">4th Year</SelectItem>
                                    </SelectContent>
                                  </Select>
                                  <FormMessage />
                                </FormItem>
                              )}
                            />
                          </motion.div>
                        )}
                      </AnimatePresence>

                      <motion.div
                        custom={4}
                        initial="hidden"
                        whileInView="visible"
                        viewport={{ once: true }}
                        variants={formFieldVariants}
                      >
                        <FormField
                          control={form.control}
                          name="institution"
                          render={({ field }) => (
                            <FormItem>
                              <FormLabel>Institution / Organization</FormLabel>
                              <FormControl>
                                <Input 
                                  placeholder="Your school, college or organization" 
                                  {...field} 
                                  data-testid="input-institution"
                                />
                              </FormControl>
                              <FormMessage />
                            </FormItem>
                          )}
                        />
                      </motion.div>

                      <motion.div 
                        className="border-t pt-6 mt-6"
                        custom={5}
                        initial="hidden"
                        whileInView="visible"
                        viewport={{ once: true }}
                        variants={formFieldVariants}
                      >
                        <div className="text-center mb-6">
                          <motion.div 
                            className="w-10 h-10 rounded-xl bg-primary/10 flex items-center justify-center mx-auto mb-3"
                            whileHover={{ scale: 1.1, rotate: 5 }}
                          >
                            <QrCode className="w-5 h-5 text-primary" />
                          </motion.div>
                          <h3 className="font-serif text-xl font-bold mb-2" data-testid="text-application-fees-form">
                            Application Fees
                          </h3>
                          <p className="text-sm text-muted-foreground font-medium">
                            Transfer 149/- to this UPI No: +91 86063 41939 or below QR
                          </p>
                        </div>

                        <div className="flex flex-col items-center gap-4">
                          <RotateIn delay={0.2}>
                            <div className="w-full max-w-xs">
                              <img 
                                src={qrCodeImage} 
                                alt="Payment QR Code" 
                                className="w-full rounded-xl border-2 border-primary/20"
                                data-testid="img-payment-qr-form"
                              />
                            </div>
                          </RotateIn>

                          <motion.div whileHover={{ scale: 1.05 }} whileTap={{ scale: 0.98 }}>
                            <Button
                              type="button"
                              variant="outline"
                              size="sm"
                              onClick={handleDownloadQR}
                              className="gap-2"
                              data-testid="button-download-qr"
                            >
                              <Download className="w-4 h-4" />
                              Download QR Code
                            </Button>
                          </motion.div>

                          <div className="text-center w-full">
                            <p className="text-sm text-muted-foreground mb-1">UPI ID:</p>
                            <p className="font-semibold text-sm break-all mb-4">javadivd8448-1@oksbi</p>

                            <p className="text-sm text-muted-foreground mb-1">Amount:</p>
                            <p className="font-bold text-lg text-primary">149.00</p>
                          </div>

                          <p className="text-center text-muted-foreground font-medium text-sm">
                            Scan to pay with any UPI app
                          </p>
                        </div>
                      </motion.div>

                      <motion.div
                        custom={6}
                        initial="hidden"
                        whileInView="visible"
                        viewport={{ once: true }}
                        variants={formFieldVariants}
                      >
                        <FormField
                          control={form.control}
                          name="paymentScreenshot"
                          render={({ field }) => (
                            <FormItem>
                              <FormLabel>Upload Payment Reference / Screenshot</FormLabel>
                              <FormControl>
                                <div className="relative">
                                  <input
                                    type="file"
                                    accept="image/*"
                                    onChange={handleFileChange}
                                    className="hidden"
                                    id="payment-screenshot"
                                    data-testid="input-payment-screenshot"
                                  />
                                  <motion.label
                                    htmlFor="payment-screenshot"
                                    className="flex items-center justify-center w-full px-4 py-3 border-2 border-dashed rounded-lg cursor-pointer bg-muted/50 border-muted-foreground/20"
                                    whileHover={{ scale: 1.01, borderColor: "hsl(var(--primary))" }}
                                    transition={{ duration: 0.2 }}
                                  >
                                    <span className="text-sm font-medium">
                                      {field.value ? "File selected" : "Choose file from gallery"}
                                    </span>
                                  </motion.label>
                                </div>
                              </FormControl>
                              <FormMessage />
                            </FormItem>
                          )}
                        />
                      </motion.div>

                      <motion.div
                        custom={7}
                        initial="hidden"
                        whileInView="visible"
                        viewport={{ once: true }}
                        variants={formFieldVariants}
                      >
                        <motion.div 
                          whileHover={{ scale: 1.03 }} 
                          whileTap={{ scale: 0.98 }}
                          transition={{ duration: 0.2 }}
                        >
                          <Button 
                            type="submit" 
                            size="lg"
                            className="w-full font-semibold"
                            disabled={mutation.isPending}
                            data-testid="button-submit-registration"
                          >
                            {mutation.isPending ? "Registering..." : "Register for KSF 2026"}
                            <Send className="w-4 h-4 ml-2" />
                          </Button>
                        </motion.div>
                      </motion.div>
                    </form>
                  </Form>
                </CardContent>
              </Card>
            </ScrollFadeUp>
          </div>
        </div>
      </section>
    </div>
  );
}

